package com.huo.utils;import com.huo.constant.Constant;import org.apache.commons.lang3.StringUtils;import java.text.DateFormat;import java.text.ParseException;import java.text.SimpleDateFormat;import java.util.Date;public class DateUtils {    /**     * 日期格式字符串转换成指定格式的日期     *      * @param dateString 2018-07-11 11:05:06     * @param formate 默认值 Constant.PATTERN_DATE yyyy-MM-dd     * @return 2018-07-11     */    public static Date covertStringToDate(String dateString, String formate) {        if (StringUtils.isBlank(formate)) {            formate = Constant.PATTERN_DATE;        }        DateFormat dateFormat = new SimpleDateFormat(formate);        try {            return dateFormat.parse(dateString);        } catch (ParseException e) {            e.printStackTrace();        }        return null;    }    /**     * 日期转换成指定格式的字符串     *      * @param date 2018-07-11T00:00:00.000+0800     * @param formate 默认值 Constant.PATTERN_DATE yyyy-MM-dd     * @return 2018-07-11     */    public static String covertDateToString(Date date, String formate) {        if (StringUtils.isBlank(formate)) {            formate = Constant.PATTERN_DATE;        }        DateFormat dateFormat = new SimpleDateFormat(formate);        return dateFormat.format(date);    }    public static void main(String[] args) {        Date date = covertStringToDate("2018-07-11 11:05:06", null);        assert date != null;        assert "2018-07-11".equals(covertDateToString(date, null));    }}